import React, { useState } from "react";

const Todo = () => {
  const [todo, setTodo] = useState({});
  const [todos, setTodos] = useState([]);
  const addTodo = ({ content, due }) => {
    const newTodo = {
      id: new Date().getTime().toString(),
      content,
      due,
      checked: false,
    };
    setTodos([...todos, newTodo]);
  };

  return (
    <section style={{ margin: "2rem" }}>
      <h1>Your TODO's</h1>
      <button onClick={addTodo}>Add</button>
      <ul>
        {todos.map(({ id, content, due, checked }) => {
          <li key={id}>
            <p>{content}</p>
            <div>{due}</div>
            <div>{checked}</div>
          </li>;
        })}
      </ul>
    </section>
  );
};

export default Todo;
