import React, { useState } from "react";
import { Button, Alert, Form } from "react-bootstrap";

const Todo = () => {
  const [todo, setTodo] = useState({});
  const [todos, setTodos] = useState([]);
  const [showCompleted, setShowCompleted] = useState(true);

  const handleAddTodo = (e) => {
    e.preventDefault();
    if (todo.content && todo.due) {
      const newTodo = {
        ...todo,
        id: new Date().getTime().toString(),
        checked: false,
      };
      setTodos([...todos, newTodo]);
      setTodo({ content: "", due: "" });
    } else {
      alert("Must fill fields to add a new todo");
    }
  };

  const handleTodoChange = (e) => {
    e.preventDefault();
    setTodo({ ...todo, [e.target.name]: e.target.value });
  };

  const clearTodos = () => {
    setTodos([]);
    alert("All items cleared");
  };
  const toggleShowCompleted = () => {
    setShowCompleted(!showCompleted);
  };
  const toggleMarkCompleted = (id) => {
    const updatedTodos = todos.map((todo) => {
      if (todo.id === id) {
        return { ...todo, checked: !todo.checked };
      }
      return todo;
    });
    console.log(id);
    console.log(updatedTodos);
    setTodos(updatedTodos);
  };
  const removeTodo = (id) => {
    setTodos(todos.filter((t) => t.id !== id));
  };

  return (
    <section style={{ margin: "2rem" }}>
      <h1>Your TODO's</h1>
      <Form>
        <Form.Group>
          <Form.Label>Enter a new Todo : </Form.Label>
          <Form.Control
            type="text"
            name="content"
            id="content"
            value={todo.content}
            onChange={handleTodoChange}
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Enter Due Date : </Form.Label>
          <Form.Control
            type="text"
            name="due"
            id="due"
            value={todo.due}
            onChange={handleTodoChange}
          />
          <Button variant="primary" onClick={handleAddTodo}>
            Add
          </Button>
        </Form.Group>
      </Form>
      <Button variant="secondary" onClick={clearTodos}>
        Clear all
      </Button>
      <Button variant="secondary" onClick={toggleShowCompleted}>
        {showCompleted ? "Hide completed" : "Show completed"}
      </Button>

      <ul>
        {todos.map(({ id, content, due, checked }) => {
          if (showCompleted || !checked) {
            return (
              <li key={id}>
                <h2>{content}</h2>
                <div>Due : {due}</div>
                <Alert variant={checked ? "success" : "danger"}>
                  Status : {checked ? "Completed" : "Uncompleted"}
                </Alert>
                <Button onClick={() => toggleMarkCompleted(id)}>
                  {checked ? "Mark uncomplete" : "Mark complete"}
                </Button>
                <Button onClick={() => removeTodo(id)}>Remove</Button>
              </li>
            );
          }
        })}
      </ul>
    </section>
  );
};

export default Todo;
