import React from "react";
import TodoCard from "./TodoCard";

import { ListGroup } from "react-bootstrap";

const TodoList = ({ todos, ...props }) => {
  const toggleMarkCompleted = props.toggleMarkCompleted;
  const removeTodo = props.removeTodo;
  return (
    <ListGroup>
      {todos.map(({ id, content, due, checked }) => {
        if (props.showCompleted || !checked) {
          return (
            <TodoCard id content due checked toggleMarkCompleted removeTodo />
          );
        }
      })}
    </ListGroup>
  );
};

export default TodoList;
