import React, { useState } from "react";

const Todo = () => {
  const [todo, setTodo] = useState({});
  const [todos, setTodos] = useState([]);
  const [showCompleted, setShowCompleted] = useState(true);

  const handleAddTodo = (e) => {
    e.preventDefault();
    if (todo.content && todo.due) {
      const newTodo = {
        ...todo,
        id: new Date().getTime().toString(),
        checked: false,
      };
      setTodos([...todos, newTodo]);
      setTodo({ content: "", due: "" });
    } else {
      alert("Must fill fields to add a new todo");
    }
  };

  const handleTodoChange = (e) => {
    e.preventDefault();
    setTodo({ ...todo, [e.target.name]: e.target.value });
  };

  const clearTodos = () => {
    setTodos([]);
    alert("All items cleared");
  };
  const toggleShowCompleted = () => {
    setShowCompleted(!showCompleted);
  };
  const toggleMarkCompleted = (id) => {
    const updatedTodos = todos.map((todo) => {
      if (todo.id === id) {
        return { ...todo, checked: !todo.completed };
      }
      return todo;
    });
    console.log(updatedTodos);
    setTodos(updatedTodos);
  };
  const removeTodo = (id) => {
    setTodos(todos.filter((t) => t.id !== id));
  };

  return (
    <section style={{ margin: "2rem" }}>
      <h1>Your TODO's</h1>
      <form>
        <label htmlFor="content">Enter a new Todo : </label>
        <input
          type="text"
          name="content"
          id="content"
          value={todo.content}
          onChange={handleTodoChange}
        />
        <label htmlFor="due">Enter Due Date : </label>
        <input
          type="text"
          name="due"
          id="due"
          value={todo.due}
          onChange={handleTodoChange}
        />
        <button onClick={handleAddTodo}>Add</button>
      </form>
      <button onClick={clearTodos}>Clear all</button>
      <button onClick={toggleShowCompleted}>
        {showCompleted ? "Hide completed" : "Show completed"}
      </button>

      <ul>
        {todos.map(({ id, content, due, checked }) => {
          if (showCompleted || !checked) {
            return (
              <li key={id}>
                <h1>{content}</h1>
                <div>{due}</div>
                <div>{checked ? "Completed" : "Uncompleted"}</div>
                <button onClick={(id) => toggleMarkCompleted(id)}>
                  {checked ? "Mark uncomplete" : "Mark complete"}
                </button>
              </li>
            );
          }
        })}
      </ul>
    </section>
  );
};

export default Todo;
