import React, { useState } from "react";

const Todo = () => {
  const [todo, setTodo] = useState({});
  const [todos, setTodos] = useState([]);
  const handleAddTodo = ({ content, due }) => {
    if (content.trim() && due.trim()) {
      const newTodo = {
        id: new Date().getTime().toString(),
        content,
        due,
        checked: false,
      };
      setTodos([...todos, newTodo]);
      setTodo({});
    } else {
      alert("Must fill fields to add a new todo");
    }
  };

  return (
    <section style={{ margin: "2rem" }}>
      <h1>Your TODO's</h1>
      <form>
        <label htmlFor="content">Enter Todo</label>
        <input
          type="text"
          name="content"
          id="content"
          value={todo.content}
          onChange={handleTodoChange}
        />
        <label htmlFor=""></label>
        <button onClick={handleAddTodo}>Add</button>
      </form>
      <ul>
        {todos.map(({ id, content, due, checked }) => {
          <li key={id}>
            <p>{content}</p>
            <div>{due}</div>
            <div>{checked}</div>
          </li>;
        })}
      </ul>
    </section>
  );
};

export default Todo;
