import React, { useEffect, useState } from "react";
import TodoList from "./TodoList";
import TodoCard from "./TodoCard";
import axios from "axios";
import {
  ListGroup,
  Container,
  Row,
  Col,
  Button,
  Alert,
  Form,
} from "react-bootstrap";

const Todo = () => {
  const [todo, setTodo] = useState({});
  const [todos, setTodos] = useState([]);
  const [showCompleted, setShowCompleted] = useState(true);
  const [sortBy, setSortBy] = useState(1); // 1: date added, 2: a-z, 3: due date
  useEffect(() => {
    const fetchCurrentTodos = async () => {
      try {
        const newTodos = await axios.get(
          `http://localhost:5000?sortby=${sortBy}`
        );
        // setTodos(newTodos.data);
        console.log(newTodos);
      } catch (err) {
        console.log(err);
      }
    };
    fetchCurrentTodos();
  }, []);
  const handleAddTodo = (e) => {
    e.preventDefault();
    if (todo.content && todo.due) {
      const newTodo = {
        ...todo,
        id: new Date().getTime().toString(),
        checked: false,
      };
      setTodos([...todos, newTodo]);
      setTodo({ content: "", due: "" });
    } else {
      alert("Must fill fields to add a new todo");
    }
  };

  const handleTodoChange = (e) => {
    e.preventDefault();
    setTodo({ ...todo, [e.target.name]: e.target.value });
  };

  const clearTodos = () => {
    setTodos([]);
    alert("All items cleared");
  };
  const toggleShowCompleted = () => {
    setShowCompleted(!showCompleted);
  };
  const toggleMarkCompleted = (id) => {
    const updatedTodos = todos.map((todo) => {
      if (todo.id === id) {
        return { ...todo, checked: !todo.checked };
      }
      return todo;
    });
    setTodos(updatedTodos);
  };
  const removeTodo = (id) => {
    setTodos(todos.filter((t) => t.id !== id));
  };
  return (
    <section className="section" style={{ margin: "2rem" }}>
      <Container>
        <h1>Your TODO's</h1>

        <Form>
          <Row>
            <Col>
              <Form.Group>
                <Form.Label>Enter a new Todo : </Form.Label>
                <Form.Control
                  type="text"
                  name="content"
                  id="content"
                  value={todo.content}
                  onChange={handleTodoChange}
                />
              </Form.Group>
            </Col>
            <Col>
              <Form.Group>
                <Form.Label>Enter Due Date : </Form.Label>
                <Form.Control
                  type="date"
                  name="due"
                  id="due"
                  value={todo.due}
                  onChange={handleTodoChange}
                />
              </Form.Group>
            </Col>
          </Row>
          <Button variant="primary" onClick={handleAddTodo}>
            Add
          </Button>
        </Form>

        <Button
          style={{ marginRight: "0.5rem" }}
          variant="secondary"
          onClick={clearTodos}
        >
          Clear all
        </Button>
        <Button variant="secondary" onClick={toggleShowCompleted}>
          {showCompleted ? "Hide completed" : "Show completed"}
        </Button>

        {/* <TodoList
          todos={todos}
          showCompleted={showCompleted}
          toggleMarkCompleted={toggleMarkCompleted}
          removeTodo={removeTodo}
        /> */}
        <ListGroup>
          {todos.map(({ id, content, due, checked }) => {
            if (showCompleted || !checked) {
              return (
                <TodoCard
                  key={id}
                  content={content}
                  due={due}
                  checked={checked}
                  toggleMarkCompleted={toggleMarkCompleted}
                  removeTodo={removeTodo}
                />
              );
            }
          })}
        </ListGroup>
      </Container>
    </section>
  );
};

export default Todo;
