import React from "react";
import { ListGroup, Button, Alert } from "react-bootstrap";

const TodoCard = (props) => {
  return (
    <ListGroup.Item>
      <h2>{props.content}</h2>
      <div>Due : {props.due}</div>
      <Alert variant={props.checked ? "success" : "danger"}>
        Status : {props.checked ? "Completed" : "Uncompleted"}
      </Alert>
      <Button onClick={() => props.toggleMarkCompleted(props.id)}>
        {props.checked ? "Mark uncomplete" : "Mark complete"}
      </Button>
      <Button onClick={() => props.removeTodo(props.id)}>Remove</Button>
    </ListGroup.Item>
  );
};

export default TodoCard;
