import React from "react";
import { ListGroup, Button, Alert } from "react-bootstrap";

const TodoCard = ({
  id,
  content,
  due,
  checked,
  toggleMarkCompleted,
  removeTodo,
}) => {
  return (
    <ListGroup.Item>
      <h2>{content}</h2>
      <div>Due : {due}</div>
      <Alert variant={checked ? "success" : "danger"}>
        Status : {checked ? "Completed" : "Uncompleted"}
      </Alert>
      <Button onClick={() => toggleMarkCompleted(id)}>
        {checked ? "Mark uncomplete" : "Mark complete"}
      </Button>
      <Button onClick={() => removeTodo(id)}>Remove</Button>
    </ListGroup.Item>
  );
};

export default TodoCard;
